// Initial state of shop
const initialStateShop = {
  cart: [],
}

const shopReducer = (state = initialStateShop, { type, payload }) => {
  switch (type) {
    case "INITIAL_CART":
      return { cart: payload.cart }
    case "SETTING_GROUP":
      return {
        ...state,
        groups : 'zxczx'
      }
    default:
      return state
  }
}

export default shopReducer
