// Initial state of shop
const initialShop = {
  shopId: localStorage.getItem("shopId"),
  customerId: localStorage.getItem("customerId"),
  name: localStorage.getItem("name"),
  phoneNumber: localStorage.getItem("phoneNumber"),
}

const authenReducer = (state = initialShop, { type, payload }) => {
  switch (type) {
    case type === "ADD_TO_SESSION_SHOP":
      return {
        shopId: payload.shopId,
        customerId: payload.customerId,
        name: payload.name,
        phoneNumber: payload.phoneNumber,
      }
    case type === "CLEAR_SESSION_SHOP":
      removeItem.getItem("shopId")
      removeItem.getItem("customerId")
      removeItem.getItem("name")
      removeItem.getItem("phoneNumber")
      return {
        shopId: null,
        customerId: null,
        name: null,
        phoneNumber: null,
      }
    default:
      return state
  }
}

export default authenReducer
