import { loginShop } from "../../lib/api_shop"
import { loginCustomer } from "../../lib/api_customer"

export const loginAction = (phoneNumber, typeLogin) => {
  return async dispatch => {
    try {
      if (typeLogin === "SHOP") {
        const session = await loginShop(phoneNumber)
      } else {
        const session = await loginCustomer(phoneNumber)
      }
      dispatch({
        type: "ADD_TO_SESSION_SHOP",
        payload: {
          shopId: session.shopId,
          phoneNumber: session.phoneNumber,
        },
      })
      dispatch({ type: "SUCCESS" })
    } catch (error) {
      dispatch({
        type: "ERROR",
        payload: { errorMessage: error.message },
      })
      dispatch({ type: "CLEAR_SESSION_SHOP" })
    }
    dispatch({ type: "CLEAR_STATUS" })
  }
}

export const logoutAction = () => {
  return dispatch => {
    dispatch({ type: "CLEAR_SESSION_SHOP" })
  }
}
