import { Modal, Button, Image, Form, Icon, Label } from "semantic-ui-react"
import { forwardRef, useImperativeHandle, useRef, useState } from "react"

const ModifyStoreModal = forwardRef((props, ref) => {
  const { image, name, phoneNumber } = props.shopInfo

  const [isOpen, setIsOpen] = useState(false)
  const [selectedFile, setSelectedFile] = useState(null)
  const [newName, setNewName] = useState(name)
  const [newPhoneNumber, setNewPhoneNumber] = useState(phoneNumber)
  const inputFileRef = useRef(null)

  useImperativeHandle(ref, () => ({
    open() {
      setIsOpen(true)
    },
  }))

  const chooseFile = e => {
    /*Selected files data can be collected here.*/
    setSelectedFile(e.target.files[0])
    console.log(e.target.files[0])
  }
  const requestChooseFile = () => {
    /*Collecting node-element and performing click*/
    inputFileRef.current.click()
  }

  const editProfile = () => {
    var formData = new FormData()
    formData.append("Name", newName)
    formData.append("PhoneNumber", phoneNumber)
    formData.append("NewPhoneNumber", newPhoneNumber)
    formData.append("Logo", selectedFile)

    setIsOpen(false)
  }
  return (
    <Modal
      onClose={() => setIsOpen(false)}
      onOpen={() => setIsOpen(true)}
      open={isOpen}
      className="store-modify-modal"
    >
      <Modal.Header>Modify Store Information</Modal.Header>
      <Modal.Content image>
        <Image rounded fluid src={`data:image/jpeg;base64,${image}`} wrapped />
        <Modal.Description>
          <Form size={"small"}>
            <Form.Field>
              <label>Name</label>
              <input
                placeholder="Name"
                value={newName}
                onChange={e => {
                  setNewName(e.target.value)
                }}
              />
            </Form.Field>
            <Form.Field>
              <label>Phone Number</label>
              <input
                placeholder="Phone"
                value={newPhoneNumber}
                onChange={e => {
                  setNewPhoneNumber(e.target.value)
                }}
              />
            </Form.Field>
            {/* <Form.Field>
              <label>Address</label>
              <input placeholder="Address" value={address} />
            </Form.Field> */}
            <Form.Field>
              <Button
                as="div"
                labelPosition="right"
                onClick={requestChooseFile}
              >
                <Button>
                  <Icon name="upload" />
                  Upload File
                </Button>
                <Label basic pointing="left">
                  Please select image
                </Label>
              </Button>
              <input
                style={{ display: "none" }}
                type="file"
                ref={inputFileRef}
                onChange={chooseFile}
                accept="image/*"
              />
            </Form.Field>
          </Form>
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        <Button color="black" onClick={() => setIsOpen(false)}>
          Close
        </Button>
        <Button
          content="Submit"
          labelPosition="right"
          icon="checkmark"
          onClick={editProfile}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
})

export default ModifyStoreModal
