import { useParams } from "react-router"
import MenuItemList from "./../components/MenuItemList"
import { Grid, Header } from "semantic-ui-react"
import Cart from "../components/Cart"
import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { checkExistCartCustomerWithShop } from "../store/actions/shop-action"

const Store = () => {
  const dispatch = useDispatch()
  const auth = useSelector(state => state.auth)
  const shop = useSelector(state => state.shop.cart)

  const { shopId } = useParams()

  const addToCart = id => {
    console.log(id)
    console.log(shop)
  }

  useEffect(() => {
    // URL store/yyyy
    dispatch(checkExistCartCustomerWithShop(auth.customerId, shopId))

    // URL cart/yyyy
  }, [dispatch, auth.customerId, shopId])

  return (
    <>
      {!!shop.shop && <Header size="medium">Shop {shop.shop.name}</Header>}
      {shop.shop && (
        <Grid>
          <Grid.Column width={10}>
            {shop.shop.items && (
              <MenuItemList
                items={shop.shop.items}
                addToCart={addToCart}
              ></MenuItemList>
            )}
            {shop.shop.items.length === 0 && <h1>Don't have any items</h1>}
          </Grid.Column>

          <Grid.Column width={6}>
            <Cart cart={shop}></Cart>
          </Grid.Column>
        </Grid>
      )}
    </>
  )
}

export default Store
