import { useState } from "react"
import { useSelector } from "react-redux"
import { Button, Divider, Header, Label, List } from "semantic-ui-react"
import { formatCurrency } from "./../helpers/number-helper"

const Cart = ({ cart }) => {
  const authPerson = useSelector(state => state.auth)
  // const { id, groups, subtotal, discount, total } = cart
  const { cartId, itemsInCart, totalPrice, customerId } = cart

  return (
    <>
      <Header>Cart {cartId}</Header>

      <List divided selection>
        <List.Item className="total">
          Total
          <Label horizontal style={{ float: "right" }}>
            {formatCurrency(totalPrice || 0)}
          </Label>
        </List.Item>
      </List>
      <Button
        basic
        content="Submit"
        labelPosition="left"
        icon="thumbs up outline"
        color="green"
        disabled={
          !itemsInCart.length > 0 || authPerson.customerId !== customerId
        }
        style={{ marginTop: 15, width: "100%" }}
      />
      <Divider></Divider>
      {itemsInCart &&
        itemsInCart.map(item => (
          <CartItemGroup key={item.id} group={itemsInCart}></CartItemGroup>
        ))}
    </>
  )
}

export default Cart
