import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"
import { Button, Grid, Icon, Image, List } from "semantic-ui-react"
import { formatCurrency } from "./../helpers/number-helper"
import { activeThisItem } from "../store/actions/admin-action"
import { useState } from "react"
import { useEffect } from "react"

const MenuItem = ({ item, viewDetail, addToCart }) => {
  const [isClickActive, setIsClickActive] = useState(false)
  const { image, name, price, itemId, isActive } = item
  const dispatch = useDispatch()
  const param = useParams()

  const notification = useSelector(state => state.notification)
  const { toastSuccess, toastError, toastInfo } = useToast()

  const activeItemHandle = () => {
    dispatch(activeThisItem(param.shopId, itemId))
    setIsClickActive(true)
  }

  useEffect(() => {
    if (isClickActive) {
      if (notification.status === "completed" && !notification.error) {
        toastSuccess("Update is success !")
        dispatch(getShopDetail(shopId))
        setIsOpen(false)
        setIsClickUpd(false)
        setSelectedFile(null)
      }
      if (notification.status === "completed" && notification.error) {
        toastError("Update is wrong !")
        setIsClickUpd(false)
      }
    }
  }, [])
  return (
    <List.Item className="menu-item">
      <List.Content>
        <Grid>
          <Grid.Column width={4}>
            <Image rounded src={`data:image/jpeg;base64,${image}`} />
          </Grid.Column>
          <Grid.Column width={10}>
            <List.Header as="a">{name}</List.Header>
            <List.Header>{formatCurrency(price)}</List.Header>
            {/* <List.Description>{description}</List.Description> */}
          </Grid.Column>
          <Grid.Column width={2}>
            <div className="menu-item_actions">
              {isActive && viewDetail && (
                <>
                  <Button
                    icon
                    color="blue"
                    onClick={() => viewDetail(itemId)}
                    title="Modify Item"
                  >
                    <Icon name="pencil" />
                  </Button>
                  <Button icon color="red" title="Delete Item">
                    <Icon name="delete" />
                  </Button>
                </>
              )}
              {!isActive && (
                <Button
                  icon
                  color="green"
                  onClick={activeItemHandle}
                  title="Action Item"
                >
                  <Icon name="check" />
                </Button>
              )}

              {addToCart && (
                <Button
                  icon
                  color="green"
                  onClick={() => addToCart(itemId)}
                  title="Add to Cart"
                >
                  <Icon name="cart plus" />
                </Button>
              )}
            </div>
          </Grid.Column>
        </Grid>
      </List.Content>
    </List.Item>
  )
}

export default MenuItem
