import { useParams } from "react-router"
import MenuItemList from "./../components/MenuItemList"
import { Grid, Header } from "semantic-ui-react"
import Cart from "../components/Cart"
import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import {
  checkExistCartCustomerWithShop,
  addItemToCart,
  removeItemFromCart,
} from "../store/actions/shop-action"
import { useState } from "react"
import useToast from "../hooks/useToast"

const Store = () => {
  const { toastSuccess, toastError } = useToast()
  const [isClick, setIsClick] = useState(false)
  const dispatch = useDispatch()
  const auth = useSelector(state => state.auth)
  const shop = useSelector(state => state.shop.cart)
  const notification = useSelector(state => state.notification)

  const { shopId } = useParams()

  const addToCart = id => {
    dispatch(addItemToCart(auth.customerId, id, shop.cartId))
    setIsClick(true)
  }

  const removeItem = id => {
    dispatch(removeItemFromCart(auth.customerId, id, shop.cartId))
    setIsClick(true)
  }

  useEffect(() => {
    if (isClick) {
      if (notification.status === "completed" && !notification.error) {
        toastSuccess("Food is added or removed into cart")
        setIsClick(false)
      }
      if (notification.status === "completed" && notification.error) {
        toastError("Add or remove food have some problem")
        setIsClick(false)
      }
    }
  }, [notification, isClick, toastSuccess, toastError])

  useEffect(() => {
    // URL store/yyyy
    dispatch(checkExistCartCustomerWithShop(auth.customerId, shopId))

    // URL cart/yyyy
  }, [dispatch, auth.customerId, shopId])

  return (
    <>
      {!!shop.shop && <Header size="medium">Shop {shop.shop.name}</Header>}
      {shop.shop && (
        <Grid>
          <Grid.Column width={10}>
            {shop.shop.items && (
              <MenuItemList
                items={shop.shop.items}
                addToCart={addToCart}
              ></MenuItemList>
            )}
            {shop.shop.items.length === 0 && <h1>Don't have any items</h1>}
          </Grid.Column>

          <Grid.Column width={6}>
            <Cart cart={shop} removeItem={removeItem}></Cart>
          </Grid.Column>
        </Grid>
      )}
    </>
  )
}

export default Store
