import React from "react";
import Router from "./routers/router";
import { ToastContainer } from "react-toastify";
import { LogLevel, HubConnectionBuilder } from "@microsoft/signalr";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { initialCartData } from "./store/actions/shop-action";

function App() {
  const dispatch = useDispatch();
  /* ==================
      SignalR Functions here 
      =================== */

  useEffect(() => {
    startCons();
  });
  const startCons = async () => {
    const connection = new HubConnectionBuilder()
      .withUrl("http://localhost:8080/hubs/cart?cart=44a39f", {
        withCredentials: false,
      })
      .configureLogging(LogLevel.Information)
      .build();

    try {
      await connection.start();
    } catch (e) {
      console.log(e);
    }

    connection.on("AddItemToCart", async (message) => {
      await dispatch(initialCartData(message.cartId));
    });
    // connection.on("UnsubmitItems", (message) => {
    //   console.log("you just unsubmtited cart id: " + message);
    // });
  };
  return (
    <React.Fragment>
      <Router></Router>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </React.Fragment>
  );
}

export default App;
