import MenuDetailModal from "./ViewMenu/MenuDetailModal"
import MenuItemList from "../components/MenuItemList"
import { generateMenu } from "../helpers/fake-data-helper"
import SectionHeader from "../components/SectionHeader"
import { useEffect, useRef, useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { getShopDetail } from "../store/actions/admin-action"
import { useParams } from "react-router-dom"

const ViewMenu = () => {
  const param = useParams()
  const shopDetail = useSelector(state => state.admin.shop)

  console.log(shopDetail.items)
  const dispatch = useDispatch()
  const modalRef = useRef(null)

  const viewDetail = id => {
    modalRef.current.open(id)
  }

  const addItem = () => {
    modalRef.current.open()
  }

  useEffect(() => {
    dispatch(getShopDetail(param.shopId))
  }, [param, dispatch])

  return (
    <>
      <SectionHeader
        title="View Menu"
        addItem={() => addItem()}
      ></SectionHeader>
      {rowData && (
        <MenuItemList items={rowData} viewDetail={viewDetail}></MenuItemList>
      )}

      <MenuDetailModal ref={modalRef}></MenuDetailModal>
    </>
  )
}

export default ViewMenu
