import { useRef } from "react"
import { Button, Image } from "semantic-ui-react"
import StoreInforField from "../../components/StoreInforField"
import { useSelector } from "react-redux"
import { useParams } from "react-router-dom"

const LeftSideBar = () => {
  const param = useParams()
  const shopInfo = useSelector(state => state.shop.cart)

  const id = param.shopId
  const { image, name, phoneNumber } = shopInfo
  const modalRef = useRef(null)

  const viewShopProfile = id => {
    modalRef.current.open(id)
  }

  const share = () => {}

  const copy = () => {}

  return (
    <div className="admin-layout_side-bar">
      <StoreInforField
        icon="linkify"
        title="Link"
        label="todo"
        link="todo"
      ></StoreInforField>

      <Button
        basic
        content="Share"
        labelPosition="left"
        icon="share alternate"
        onClick={share}
        color="green"
        style={{ marginTop: 15, width: "100%" }}
      />

      <Button
        basic
        content="Copy Link"
        labelPosition="left"
        icon="linkify"
        onClick={copy}
        color="brown"
        style={{ marginTop: 15, width: "100%" }}
      />
    </div>
  )
}

export default LeftSideBar
