import { forwardRef, useImperativeHandle, useState, useEffect } from "react"
import { Button, Modal, Form } from "semantic-ui-react"
import ViewContent from "./ViewContent"
import { useDispatch, useSelector } from "react-redux"
import { newOrder } from "../../store/actions/shop-action"
import { useHistory } from "react-router"
import useToast from "../../hooks/useToast"

const CheckOutModal = forwardRef((props, ref) => {
  const [open, setOpen] = useState(false)
  const [order, setOrder] = useState(null)
  const [isOrder, setIsOrder] = useState(false)
  const [infomation, setInfomation] = useState(null)
  const history = useHistory()

  const notification = useSelector(state => state.notification)
  const { toastSuccess, toastError } = useToast()

  const dispatch = useDispatch()
  useImperativeHandle(ref, () => ({
    open() {
      setOpen(true)
    },
  }))

  const checkout = () => {
    setOrder(dispatch(newOrder(props.cartId, infomation)))
    setIsOrder(true)
  }

  useEffect(() => {
    if (isOrder) {
      if (notification.status === "completed" && !notification.error) {
        toastSuccess("Order is success !")
        setIsOrder(false)
        history.push("/order/" + order)
      }
      if (notification.status === "completed" && notification.error) {
        setIsOrder(false)
        toastError("Order is wrong !")
      }
    }
  }, [notification, history, toastSuccess, toastError, isOrder, order])

  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      style={{
        margin: 0,
      }}
    >
      <Modal.Header>Check Out</Modal.Header>
      <Modal.Content image>
        <ViewContent groups={props.groups} totalPrice={props.totalPrice} />
      </Modal.Content>

      <Form size={"small"}>
        <Form.Field>
          <label>Input infomation</label>
          <textarea
            onChange={e => {
              setInfomation(e.target.value)
            }}
            placeholder="Input infomation"
          />
        </Form.Field>
      </Form>
      <Modal.Actions>
        <Button
          content="Continue Shopping"
          labelPosition="right"
          icon="shop"
          onClick={() => setOpen(false)}
        />

        <Button
          content="Check Out"
          labelPosition="right"
          icon="arrow right"
          onClick={checkout}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
})

export default CheckOutModal
