import { Modal, Button, Image, Form, Icon, Label } from "semantic-ui-react"
import { forwardRef, useImperativeHandle, useRef, useState } from "react"
import { useDispatch } from "react-redux"
import { updateInfomationItem } from "../../store/actions/admin-action"

const MenuDetailModal = forwardRef((props, ref) => {
  const dispatch = useDispatch()
  const [isOpen, setIsOpen] = useState(false)
  const [isClickUpd, setIsClickUpd] = useState(false)
  const [selectedFile, setSelectedFile] = useState(null)
  const [menu, setItem] = useState({})
  const inputFileRef = useRef(null)

  useImperativeHandle(ref, () => ({
    open(id) {
      setIsOpen(true)

      if (id) {
        const item = props.items
          .filter(item => item.itemId === id)
          .map(filteredItem => {
            return filteredItem
          })

        const url = `data:image/png;base64,${item[0].image}`

        fetch(url)
          .then(res => res.blob())
          .then(response => {
            setItem({
              id: item[0].itemId,
              shopId: item[0].shopId,
              name: item[0].name,
              price: item[0].price,
              image: URL.createObjectURL(response),
            })
          })
      } else {
        setItem({})
      }
    },
  }))

  const chooseFile = e => {
    /*Selected files data can be collected here.*/
    setSelectedFile(e.target.files[0])
    setItem(state => ({
      ...state,
      image: URL.createObjectURL(e.target.files[0]),
    }))
  }
  const requestChooseFile = () => {
    /*Collecting node-element and performing click*/
    inputFileRef.current.click()
  }

  const { id, image, name, price, shopId } = menu

  const editItem = () => {
    var formData = new FormData()
    formData.append("ShopId", shopId)
    formData.append("ItemId", id)
    formData.append("Name", name)
    formData.append("Price", price)
    formData.append("Image", selectedFile)

    dispatch(updateInfomationItem(formData))
    setIsClickUpd(true)
  }

  useEffect(() => {
    if (isClickUpd) {
      if (notification.status === "completed" && !notification.error) {
        toastSuccess("Update is success !")
        dispatch(getShopDetail(id))
        setIsOpen(false)
        setIsClickUpd(false)
      }
      if (notification.status === "completed" && notification.error) {
        toastError("Update is wrong !")
        setIsClickUpd(false)
      }
    }
  }, [
    isClickUpd,
    notification,
    id,
    dispatch,
    toastSuccess,
    toastError,
    toastInfo,
  ])
  return (
    <Modal
      onClose={() => setIsOpen(false)}
      onOpen={() => setIsOpen(true)}
      open={isOpen}
      className="menu-modify-modal"
    >
      <Modal.Header>Modify Menu</Modal.Header>
      <Modal.Content image>
        {menu && (
          <>
            <Image
              rounded
              fluid
              src={image || "https://dummyimage.com/900x900/ecf0f1/aaa"}
              wrapped
            />
            <Modal.Description>
              <Form size={"small"}>
                <Form.Field>
                  <label>Name</label>
                  <input placeholder="Name" value={name} />
                </Form.Field>
                <Form.Field>
                  <label>Price</label>
                  <input placeholder="Price" value={price} />
                </Form.Field>
                <Form.Field>
                  <Button
                    as="div"
                    labelPosition="right"
                    onClick={requestChooseFile}
                  >
                    <Button>
                      <Icon name="upload" />
                      Upload File
                    </Button>
                    <Label basic pointing="left">
                      {image || "Please select image"}
                    </Label>
                  </Button>
                  <input
                    style={{ display: "none" }}
                    type="file"
                    ref={inputFileRef}
                    onChange={chooseFile}
                    accept="image/*"
                  />
                </Form.Field>
              </Form>
            </Modal.Description>
          </>
        )}
      </Modal.Content>
      <Modal.Actions>
        <Button color="black" onClick={() => setIsOpen(false)}>
          Close
        </Button>
        <Button
          content="Submit"
          labelPosition="right"
          icon="checkmark"
          onClick={editItem}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
})

export default MenuDetailModal
