// Initial state of shop
const initialStateAdmin = {
  shop: [],
}

const adminReducer = (state = initialStateAdmin, { type, payload }) => {
  switch (type) {
    case "INITIAL_SHOP":
      return { shop: payload.shop }
    default:
      return state
  }
}

export default adminReducer
