// Initial state of shop
const initialShop = {
  shopId: localStorage.getItem("shopId"),
  phoneNumber: localStorage.getItem("shopPhoneNumber"),
  isLoggedInShop: !!localStorage.getItem("shopId"),
}

const authenReducer = (state = initialShop, { type, payload }) => {
  switch (type) {
    case action.type === "ADD_TO_SESSION_SHOP":
      return {
        shopId: payload.shopId,
        phoneNumber: payload.phoneNumber,
        isLoggedInShop: payload.isLoggedInShop,
      }
    case action.type === "CLEAR_SESSION_SHOP":
      return {
        shopId: null,
        phoneNumber: null,
        isLoggedInShop: false,
      }
    default:
      return state
  }
}

export default authenReducer
