import { useParams } from "react-router"
import { useState } from "react"
import MenuItemList from "./../components/MenuItemList"
import { Grid, Header } from "semantic-ui-react"
import Cart from "../components/Cart"
import { generateCart, generateMenu } from "../helpers/fake-data-helper"
import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { checkExistCartCustomerWithShop } from "../store/actions/shop-action"

const Store = () => {
  const dispatch = useDispatch()
  const auth = useSelector(state => state.auth)
  const shop = useSelector(state => state.shop.cart)
  const menuFake = generateMenu()
  const cartFake = generateCart(menuFake)

  const { shopId } = useParams()
  const [cart] = useState(cartFake)

  const addToCart = id => {}

  useEffect(() => {
    dispatch(checkExistCartCustomerWithShop(auth.customerId, shopId))
  }, [dispatch, auth.customerId, shopId])

  console.log(shop)
  return (
    <>
      {!!shop.shop && <Header size="medium">Shop {shop.shop.name}</Header>}
      <Grid>
        <Grid.Column width={3}>

        </Grid.Column>
        {shop.shop && (
          <Grid.Column width={8}>
            {shop.shop.items && (
              <MenuItemList
                items={shop.shop.items}
                addToCart={addToCart}
              ></MenuItemList>
            )}
            {shop.shop.items.length === 0 && <h1>Don't have menu</h1>}
          </Grid.Column>
        )}
        <Grid.Column width={5}>
          <Cart cart={cart}></Cart>
        </Grid.Column>
      </Grid>
    </>
  )
}

export default Store
