import { Modal, Header, Icon, Button, Divider } from "semantic-ui-react";
import { forwardRef, useImperativeHandle, useState } from "react";

const ShareModal = forwardRef((props, ref) => {
  const [isOpen, setIsOpen] = useState(false);

  useImperativeHandle(ref, () => ({
    open(url) {
      setIsOpen(true);
    },
  }));

  return (
    <Modal
      basic
      onClose={() => setIsOpen(false)}
      onOpen={() => setIsOpen(true)}
      open={isOpen}
      size="small"
      className="store-modify-modal"
    >
      <Header size="small" icon>
        Share To Customer
        <Icon name="share" />
      </Header>
      <Divider />
      <Modal.Content>
        <p>
          Your inbox is getting full, would you like us to enable automatic
          archiving of old messages?
        </p>
      </Modal.Content>
      <Modal.Actions>
        <Button basic color="blue" inverted onClick={() => setIsOpen(false)}>
          <Icon name="copy" /> Copy
        </Button>
      </Modal.Actions>
    </Modal>
  );
});

export default ShareModal;
