import { useHistory } from "react-router"
import { useState } from "react"
import {
  Button,
  Image,
  Form,
  Grid,
  Segment,
  Container,
  Divider,
  Label,
  Icon,
} from "semantic-ui-react"
import { useDispatch, useSelector } from "react-redux"
import { loginAction } from "../store/actions/auth-action"
import { useRef } from "react"
import { useEffect } from "react"
import useToast from "../hooks/useToast"

const Login = () => {
  const notification = useSelector(state => state.notification)
  const { toastSuccess, toastError } = useToast()
  const dispatch = useDispatch()
  const refPhoneNumber = useRef()
  const history = useHistory()
  const [isShop, setIsShop] = useState(true)

  const signUp = () => {
    history.push("/sign-up")
  }

  const toggleView = () => {
    setIsShop(!isShop)
  }

  const submit = () => {
    if (isShop) {
      dispatch(loginAction(refPhoneNumber.current.value))
      // history.push("/admin")
    } else {
      history.push("/")
    }
  }

  useEffect(() => {
    if (notification.status === "completed" && !notification.error) {
      toastSuccess("Login is success")
    } else {
      toastError(notification.error)
    }
  }, [notification.status])

  const label = isShop ? "Sign in as customer?" : "Sign in as store owner?"

  return (
    <Container className="auth-form">
      <Image src="/logo/logo64.png" centered />
      <Grid columns="equal">
        <Grid.Column></Grid.Column>
        <Grid.Column width={6}>
          <Label as="a" style={{ width: "100%" }} onClick={toggleView}>
            <Icon name="question circle" /> {label}
          </Label>
          <Divider />

          <Segment raised>
            <Form>
              <Form.Field>
                <label>Phone Number</label>
                <input ref={refPhoneNumber} placeholder="Phone Number" />
              </Form.Field>
              <Button type="submit" color="green" fluid onClick={submit}>
                Submit
              </Button>
            </Form>

            <Divider />
            <Label
              as="a"
              basic
              color="grey"
              style={{ width: "100%" }}
              onClick={signUp}
            >
              <Icon name="user plus" /> Don't have account? Register now
            </Label>
          </Segment>
        </Grid.Column>
        <Grid.Column></Grid.Column>
      </Grid>
    </Container>
  )
}

export default Login
