// Initial state of shop
const initialShop = {
  id: localStorage.getItem("id"),
  phoneNumber: localStorage.getItem("phoneNumber"),
  isLoggedInShop: !!localStorage.getItem("id"),
  isLoggedInCustomer: !!localStorage.getItem("id"),
}

const authenReducer = (state = initialShop, { type, payload }) => {
  switch (type) {
    case type === "ADD_TO_SESSION_SHOP":
      return {
        shopId: payload.shopId,
        phoneNumber: payload.phoneNumber,
        isLoggedInShop: payload.isLoggedInShop,
      }
    case type === "CLEAR_SESSION_SHOP":
      return {
        id: null,
        name: null,
        phoneNumber: null,
        isLoggedInShop: false,
        isLoggedInCustomer: false,
      }
    default:
      return state
  }
}

export default authenReducer
