    }

    dispatch({ type: "CLEAR_STATUS" })
  }
}

export const activeThisItem = (shopId, itemId) => {
  return async dispatch => {
    dispatch({ type: "SEND" })
    try {
      await activeItem(shopId, itemId)
      dispatch({ type: "SUCCESS" })
