import { Button, Divider, Header, Label, List } from "semantic-ui-react"
import CartItemGroup from "./CartItemGroup"
import { formatCurrency } from "./../helpers/number-helper"
import { useSelector } from "react-redux"
import { useEffect } from "react"
import { useState } from "react"

const Cart = ({ cart }) => {
  const [groups, setGroups] = useState([])
  const userAuth = useSelector(state => state.auth)
  const { cartId, itemsInCart, totalPrice, customerId } = cart

  useEffect(() => {
    let userId = null
    let groupTemp = []
    for (const key in itemsInCart) {
      if (userId === itemsInCart[key].customerId) {
        const index = groupTemp.findIndex(group => group.userId === userId)
        groupTemp[index] = {
          ...groupTemp[index],
          items: groupTemp[index].push(itemsInCart[key]),
        }
      } else {
        groupTemp.push({
          id: +key,
          userId: itemsInCart[key].customerId,
          userName: itemsInCart[key].customerName,
          items: [itemsInCart[key]],
        })
        userId = itemsInCart[key].customerId
      }
      console.log(groupTemp)
    }
  })

  return (
    <>
      <Header>Cart {cartId}</Header>

      <List divided selection>
        <List.Item className="total">
          Total
          <Label horizontal style={{ float: "right" }}>
            {formatCurrency(totalPrice || 0)}
          </Label>
        </List.Item>
      </List>
      <Button
        basic
        content="Submit"
        labelPosition="left"
        icon="thumbs up outline"
        color="green"
        disabled={
          customerId !== userAuth.customerId && itemsInCart.length === 0
        }
        style={{ marginTop: 15, width: "100%" }}
      />
      <Divider></Divider>
      {groups &&
        groups.map(group => (
          <CartItemGroup key={group.itemId} group={group}></CartItemGroup>
        ))}
    </>
  )
}

export default Cart
