import { useState } from "react"
import { useCallback } from "react"
import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { useHistory } from "react-router-dom"
import { formatCurrency } from "../helpers/number-helper"
import { fetchOrder } from "../store/actions/shop-action"

const Order = () => {
  const authUser = useSelector(state => state.auth)
  const notification = useSelector(state => state.notification)
  const dispatch = useDispatch()

  const history = useHistory()
  const [listOrder, setListOrder] = useState([])

  const fetchListOrder = useCallback(async () => {
    const response = await dispatch(fetchOrder(authUser.customerId))
    setListOrder(response)
  }, [authUser.customerId, dispatch])
  useEffect(() => {
    fetchListOrder()
  }, [fetchListOrder])

  return (
    <>
      {listOrder.orders &&
        listOrder.orders.map(order => (
          <div className="ui middle aligned divided list">
            <div className="item">
              <div className="right floated content">
                <div
                  className="ui button"
                  onClick={() => history.push("/order/" + order.orderId)}
                >
                  View Order
                </div>
              </div>

              <div style={{ display: "inline" }}>{order.orderId}</div>
              <div style={{ display: "inline" }}>{order.status}</div>
              <div sstyle={{ display: "inline" }}>
                {formatCurrency(order.totalPrice)}
              </div>
            </div>
          </div>
        ))}
    </>
  )
}

export default Order
