import { useState } from "react"
import { Menu, Segment } from "semantic-ui-react"
import BillName from "./BillName"
import BillPersonDetail from "./BillPersonDetail"

const BillPerson = ({ orderPerson }) => {
  const [activePerson, setActivePerson] = useState()

  const handlePersonClick = name => {
    setActivePerson(name)
  }
  return (
    <div>
      <Menu attached="top" tabular>
        {orderPerson.map(person => (
          <BillName
            handlePersonClick={handlePersonClick}
            activePerson={activePerson}
            key={person.userId}
            id={person.userId}
            userName={person.userName}
          />
        ))}
      </Menu>

      <Segment attached="bottom">
        {orderPerson.filter(
          (order => order.userId.includes(activePerson)).map(filteredOrder => (
            <BillPersonDetail items={filteredOrder.items} />
          ))
        )}
      </Segment>
    </div>
  )
}
export default BillPerson
