import { loginShop } from "./lib/api_shop"
import useToast from "./hooks/useToast"

export const loginAction = phoneNumber => {
  const { toastSuccess } = useToast()

  return async dispatch => {
    try {
      const session = await loginShop(phoneNumber)
      dispatch({
        type: "ADD_TO_SESSION_SHOP",
        payload: {
          shopId: session.shopId,
          name: session.name,
          phoneNumber: session.phoneNumber,
        },
      })

      localStorage.setItem("shopId", session.shopId)
      localStorage.setItem("shopPhoneNumber", session.phoneNumber)

      toastSuccess("Login successfully")
    } catch (error) {
      toastError("Login is failed")
      dispatch({ type: "CLEAR_SESSION_SHOP" })
    }
  }
}

export const logoutAction = () => {
  return dispatch => {
    dispatch({ type: "CLEAR_SESSION_SHOP" })
  }
}

export default authShopReducer
