import { useParams } from "react-router"
import { useState } from "react"
import MenuItemList from "./../components/MenuItemList"
import { Grid, Header } from "semantic-ui-react"
import Cart from "../components/Cart"
import { generateCart, generateMenu } from "../helpers/fake-data-helper"
import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { checkExistCartCustomerWithShop, initialCartData } from "../store/actions/shop-action"
import { useCallback } from "react"

const Store = () => {
  const dispatch = useDispatch()
  const auth = useSelector(state => state.auth)
  const shop = useSelector(state => state.shop)
  const menuFake = generateMenu()
  const cartFake = generateCart(menuFake)

  const { shopId } = useParams()
  const [items] = useState(menuFake)
  const [cart] = useState(cartFake)
  const [responseCheck, setResponseCheck] = useState(null)

  const addToCart = id => {}

  const checkResponseExistCart = useCallback(async () => {
    const response = await dispatch(
      checkExistCartCustomerWithShop(auth.customerId, shopId)
    )
    setResponseCheck(response)
  }, [auth.customerId, shopId, dispatch])

  useEffect(() => {
    checkResponseExistCart()
  }, [checkResponseExistCart])

  useEffect(() => {
    if (!responseCheck) {
      console.log("OK")
    } else {
      dispatch(initialCartData(responseCheck.cartId))
    }
  }, [responseCheck])

  console.log(shop)
  return (
    <>
      <Header size="medium">Shop {shopId}</Header>
      <Grid>
        <Grid.Column width={12}>
          {items && (
            <MenuItemList items={items} addToCart={addToCart}></MenuItemList>
          )}
        </Grid.Column>
        <Grid.Column width={4}>
          <Cart cart={cart}></Cart>
        </Grid.Column>
      </Grid>
    </>
  )
}

export default Store
